import React from "react"
import API from "./../../API"
import Spinner from "../../shared/components/spinner"
import { gql } from "graphql-request"
import { Link } from "react-router-dom"

const query = gql`
  query goodFind($query: String) {
    GoodFind(query: $query) {
      name
      _id
      images {
        url
      }
    }
  }
`

const mapStateToProps = (state) => ({
  goodsQueryFromRedux: state.goods.goodsQuery,
})

const mapDispatchToProps = (dispatch) =>
  bindActionCreators(
    { goodsQuery: (payload) => ({ type: "QUERY_RESOLVE", payload }) },
    dispatch
  )

const SubComponent = ({ status, noData, children }) => {
  if (status === "searching") {
    return <Spinner />
  }

  if (noData) {
    return <span>No Data</span>
  }
  if (status === "idle") {
    return <span>Search data</span>
  }
  if (status === "resolved") {
    return children
  }
}

const GoodsSearch = (props) => {
  console.log(props)
  const [result, setResult] = React.useState(null)
  const [status, setStatus] = React.useState("idle")

  const onChange = (e) => {
    const searchQuery = new RegExp(e.target.value)
    setStatus("searching")
    API.request(query, {
      query: JSON.stringify([
        {
          name: String(searchQuery),
        },
      ]),
    }).then((r) => {
      setResult(r.GoodFind)
      setStatus("resolved")
    })
  }

  // console.log(result, "result", result !== null && result.length === 0);
  return (
    <div className="Container">
      <Link to="/">Go Home</Link>
      <div className="col-sm-12 col-md-4">
        <input className="form-control" type="search" onChange={onChange} />
      </div>
      <div className="col-sm-12 my-3">
        <SubComponent
          noData={result !== null && result.length === 0}
          status={status}
        >
          <div>{JSON.stringify(result)}</div>
        </SubComponent>
      </div>
    </div>
  )
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(React.memo(GoodsSearch))
