import React from "react"
import API from "./../../API"
import Spinner from "../../shared/components/spinner"
import { gql } from "graphql-request"
import { Link } from "react-router-dom"

const query = gql`
  query goodFind($query: String) {
    GoodFind(query: $query) {
      name
      _id
      images {
        url
      }
    }
  }
`

const StatusResolver = ({ status, noData, children }) => {
  if (status === "searching") {
    return <Spinner />
  }

  if (noData) {
    return <span>No Data</span>
  }
  if (status === "idle") {
    return <span>Search data</span>
  }
  if (status === "resolved") {
    return children
  }
}
const GoodsSearch = () => {
  const [result, setResult] = React.useState(null)
  const [status, setStatus] = React.useState("idle")

  const onChange = (e) => {
    const searchQuery = new RegExp(e.target.value)
    setStatus("searching")
    API.request(query, {
      query: JSON.stringify([
        {
          name: String(searchQuery),
        },
      ]),
    }).then((res) => {
      setResult(res.GoodFind)
      setStatus("resolved")
    })
  }

  return (
    <div className="Container">
      <Link to="/">Go Home</Link>
      <div className="col-sm-12 col-md-4">
        <input className="form-control" type="search" onChange={onChange} />
      </div>
      <div className="col-sm-12 my-3">
        <StatusResolver
          noData={result !== null && result.length === 0}
          status={status}
        >
          {/* <div>{JSON.stringify(result)}</div> */}
          <div class="card" style="width: 18rem;">
            {result.map((x) => (
              <>
                {/* <img
                  src={x.images[0] ? x.images[0].url : "http://"}
                  class="card-img-top"
                  alt={x._id}
                /> */}
                <div class="card-body">
                  <h5 class="card-title">{x.name}</h5>
                  <p class="card-text">
                    Some quick example text to build on the card title and make
                    up the bulk of the card's content.
                  </p>
                  <a href="#" class="btn btn-primary">
                    Go somewhere
                  </a>
                </div>
              </>
            ))}
          </div>
        </StatusResolver>
      </div>
    </div>
  )
}

export default GoodsSearch
