import React from "react"
import API from "./../../API"
import Spinner from "../../shared/components/spinner"
import { gql } from "graphql-request"
import { Link } from "react-router-dom"

const query = gql`
  query goodFind($query: String) {
    GoodFind(query: $query) {
      name
      _id
      images {
        url
      }
    }
  }
`

const GoodsSearch = () => {
  const [result, setResult] = React.useState(null)


  const handleResult = () => {

    const searchQuery = new RegExp(
      document.getElementById("goodSearchInput").value
    )
      API.request(query, {
        query: JSON.stringify([
          {
            name: searchQuery.toString(),
          },
        ]),
      }).then((res) => {
        setResult(res.GoodFind)
        
        setTimeout(()=>console.log(result),2000)
      })
  }
  
  // const handleResult = (arr) => {

  //   return arr.map((x) => (
  //     <div class="card" style="width: 18rem;">
  //       <img
  //         src={x.images[0] ? x.images[0].url : "http://"}
  //         class="card-img-top"
  //         alt={x._id}
  //       />
  //       <div class="card-body">
  //         <h5 class="card-title">{x.name}</h5>
  //         <p class="card-text">
  //           Some quick example text to build on the card title and make up the
  //           bulk of the card's content.
  //         </p>
  //         <a href="#" class="btn btn-primary">
  //           Go somewhere
  //         </a>
  //       </div>
  //     </div>
  //   ))
  // }

  return (
    <div className="Container">
      <Link to="/">Go Home</Link>
      <div className="col-sm-12 col-md-4">
        <input className="form-control" type="search" id="goodSearchInput" />
      </div>
      <button onClick={handleResult} type="button" className="btn btn-primary">
        Primary
      </button>
      <div className="col-sm-12 my-3"></div>
    </div>
  )
}

export default GoodsSearch
